import React, { useEffect, useState } from 'react'
import Item from './Item';

const productsArray = [
    {id: 1, name: 'Producto 1', description: 'Descripción producto 1', price: 100, pictureUrl: 'https://images.pexels.com/photos/1683724/pexels-photo-1683724.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500'},
    {id: 2, name: 'Producto 2', description: 'Descripción producto 1', price: 110},
    {id: 3, name: 'Producto 3', description: 'Descripción producto 1', price: 140},
    {id: 4, name: 'Producto 4', description: 'Descripción producto 1', price: 190},
];

// function getProductos(id) {
//     if (id===undefined) {
//         return productsArray
//     }else{
//         return productsArray.find( productArray=> productArray.id === id )
//     }
// }

let tarea = new Promise( (resolve)=>{
    setTimeout(()=>{resolve(productsArray)}, 3000);
} );

const ItemList = () => {
    const [products, setProducts] = useState([]);
    useEffect(() => {
        tarea.then( (x)=> setProducts(x) )
    }, [])
    
    return (
        <div>
                { products.map( (product) => { 
                return(
                    <Item key={product.id} name={product.name} pictureUrl={product.pictureUrl}/>
                    )
                }) 
                }
        </div>
    )
}

export default ItemList
