import React, { useState } from 'react';
import './body.css';

const ItemCount = () => {
    const initial = 1;
    const [count, setCount] = useState(0)
    const handleCounterClick = (number) =>{
        return () =>{
            if (number < initial ) {
                setCount(count - 1);                
            }else{
                setCount(initial +1 );
            }
        }
    }
    return (
        <div>
            <button onClick={handleCounterClick}>-</button>
            <p>{ count }</p>
            <button onClick={handleCounterClick}>+</button>
        </div>
    )
}

export default ItemCount
