import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons';
import { faMinus } from '@fortawesome/free-solid-svg-icons';
import './body.css';

const ItemCount = () => {
    const initial = 1;
    const stock = 5;
    const [count, setCount] = useState(initial)
    const handleClick = (number) => {
        return () => {
            if (number && count < stock) {
                setCount(count + 1);
            }
            else if (!number && count > initial) {
                setCount(count - 1);
            }
        }
    }
    return (
        <div class="d-grid gap-2 d-md-block itemcount">
            <button onClick={handleClick(false)} class="btn btn-primary" type="button"><FontAwesomeIcon icon={faMinus} /></button>
                <p>{ count }</p>
            <button onClick={handleClick(true)} class="btn btn-primary" type="button"><FontAwesomeIcon icon={faPlus} /></button>
        </div>
    )
}

export default ItemCount
