import React, { useState } from 'react'
import { useCartContext } from '../../context/CartContext'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrashAlt } from '@fortawesome/free-solid-svg-icons';
import './cart.css'
import { NavLink } from 'react-router-dom';
import CartForm from './CartForm';

const Cart = () => {
    
    const {clearCart, cart, removeItem} = useCartContext()
    
    let total = 0;

    const [formData, setFormData] = useState(initialState)
    const inputs = [
        {type:"text", name:"name", placeholder:"ingrese su nombre", value: formData.name},
        {type:"text", name:"lastname", placeholder:"ingrese su apellido", value: formData.lastname},
        {type:"text", name:"phone", placeholder:"ingrese su telefono", value: formData.phone},
        {type:"email", name:"email", placeholder:"ingrese su email", value: formData.email},
        {type:"email", name:"emailconfirmation", placeholder:"reingrese su email"}
    ]
    // const inputs = [
    //     {type: 'text', placeholder:"ing nombr", name:"nombre", value: formData.nombre},
    //     {type: 'text', placeholder:"ing te", name:"tel", value: formData.tel},
    //     {type: 'email', placeholder:"ing email", name:"email", value: formData.email},
    //     {type: 'email', placeholder:"confirme el mai", name:"email2", value: formData.email2}
    // ]
    
    return (
        <>{cart.length === 0 &&
        <div className="row cart">
            <img src="https://i.gifer.com/XSNt.gif" alt="No hay nada en tu carrito"/>
            <h3>No hay nada en tu carrito</h3>
            <NavLink to="/"><button type="button" className="btn btn-productos btn-lg px-4 gap-3" data-bs-container="body" data-bs-toggle="popover" data-bs-placement="top" data-bs-content="Top popover">Ver productos</button></NavLink>
        </div>
        }
        {cart.length > 0 && 
        <div className="container">
            
                <table class="table table-striped table-hover cartlistcontainer">
                    <thead>
                        <tr>
                        <th scope="col">#</th>
                        <th scope="col">Nombre</th>
                        <th scope="col">Cantidad</th>
                        <th scope="col">Precio</th>
                        <th scope="col">Total por unidad</th>
                        <th scope="col"></th>
                        </tr>
                    </thead>
                    <tbody>
                    {cart.map(element =>
                        <tr>
                        <td><img src={element.item.pictureUrl} alt={element.item.name} /></td>
                        <td>{element.item.name}</td>
                        <td>{element.qty}</td>
                        <td>{element.item.price}</td>
                        <td>{element.item.price*element.qty}</td>
                        <td><button className="btn-trash" onClick={() => removeItem(element.item.id, element.qty)}><FontAwesomeIcon icon={faTrashAlt} /></button></td>
                        </tr>
                    )}
                    </tbody>
                    <tfoot>
                        <tr>
                            <th scope="col"></th>
                            <th scope="col"></th>
                            <th scope="col"><div className="d-print-none">{cart.map(element => {
                                return total = element.item.price * element.qty + total} )}</div></th>
                            <th scope="col">Total</th>
                            <th scope="col">$ {total}</th>
                            <th scope="col"></th>
                        </tr>                        
                    </tfoot>
                </table>
                <div className="d-flex justify-content-evenly">
                    <div>
                        <button type="button" onClick={clearCart} className="btn btn-carrito btn-lg px-4 gap-3 centrar" data-bs-container="body" data-bs-toggle="popover" data-bs-placement="top" data-bs-content="Top popover">Borrar productos</button>
                    </div>
                    <div>
                        <NavLink to="/">
                        <button type="button" className="btn btn-productos btn-lg px-4 gap-3 centrar" data-bs-container="body" data-bs-toggle="popover" data-bs-placement="top" data-bs-content="Top popover">seguir comprando</button>
                        </NavLink>
                    </div>
                </div>
                <CartForm
                    formData={formData}
                    setFormData={setFormData}
                    initialState={initialState}
                    total={total}
                    // inputs={inputs} 
                    // formData={formData} 
                    // setFormData={setFormData} 
                    // initialState={initialState}
                    // total={total}
                />
        </div>
        }
        </>
    )
}

export default Cart

const initialState = {
    name: '',
    lastname: '',
    phone: '',
    emai:''
}

// const initialState = {
//     nombre: '',
//     email:'',
//     tel:''
// }
