import React, { useEffect, useState } from 'react'
import Item from './Item';
import { promiseList } from '../utils/promises';

const ItemList = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    useEffect(() => {
        promiseList.then( (x)=> setProducts(x) ).then(setLoading(false)).catch( (err)=> {console.log(err.message)} )
    }, [])
    
    return (
        <div className="row products">
            {loading ? "Cargando..." :  products.map( (product) => { 
                return(
                    <Item key={product.id} name={product.name} pictureUrl={product.pictureUrl} description={product.description} price={product.price} />
                    )
                }) 
                }
        </div>
    )
}

export default ItemList
