import React, { useState } from 'react';
import './body.css';

const ItemCount = () => {
    const initial = 1;
    const stock = 5;
    const [count, setCount] = useState(initial)
    const handleClick = (op) => {
        return () => {
            if (op && count < stock) {
                setCount(count + 1);
            }
            else if (!op && count > 1) {
                setCount(count - 1);
            }
        }
    }
    return (
        <div>
            <button onClick={handleClick(true)}>-</button>
            <p>{ count }</p>
            <button onClick={handleClick(false)}>+</button>
        </div>
    )
}

export default ItemCount
