import React, { useEffect, useState } from 'react'

const ItemList = () => {
    const productsArray = [
        {id: 1, name: 'Producto 1', description: 'Descripción producto 1', price: 100},
        {id: 2, name: 'Producto 2', description: 'Descripción producto 1', price: 110},
        {id: 3, name: 'Producto 3', description: 'Descripción producto 1', price: 140},
        {id: 4, name: 'Producto 4', description: 'Descripción producto 1', price: 190},
    ];
    
     const products = new Promise((resolve)=>{
         setTimeout(()=>{ 
             resolve(productsArray);}, 2000);
     });

    const [items, setItems] = useState([]);
     useEffect( () => {
         products.then(
             (resp) => {
                 return setItems
            }
         );
     }, [])

    return (
        <div>
            <ul>
                { products.map(product => <li key = { product.id }>{ product.name }<br/>{ product.price}</li>)}
            </ul>
        </div>
    )
}

export default ItemList
