import React, { useState } from 'react';
import './body.css';

const ItemCount = () => {
    const initial = 1;
    const stock = 5;
    const [count, setCount] = useState(initial)
    const handleCounterClick = () =>{
        return () =>{
            if (count > initial) {
                setCount(count - 1);                
            }else if ((count >= initial) && (count < stock)) {
                setCount(initial +1 );
            }
        }
    }
    return (
        <div>
            <button onClick={handleCounterClick(true)}>-</button>
            <p>{ count }</p>
            <button onClick={handleCounterClick(false)}>+</button>
        </div>
    )
}

export default ItemCount
