import React, { useEffect, useState } from 'react';
import ItemDetail from './ItemDetail';
import './body.css';
import { promiseDetail } from '../utils/promises'
import { useParams } from 'react-router-dom';

const ItemDetailContainer = () => {
    const [loading, setLoading] = useState(true)
    const [product, setProduct] = useState({})
    const {id} = useParams()
    
    useEffect(()=>{
        promiseDetail.then((x)=>{
            setProduct(x)
            setLoading(false)
        }).catch((err)=>{console.log(err.message)})
    },
    [id])
    console.log(product)

    return (
        <div className="px-4 py-5 my-5 text-center">
            {
            loading
            ?
            <h2>Cargando detalle de producto...</h2>
            :
            <ItemDetail id={product.id} name={product.name} pictureUrl={product.pictureUrl} description={product.description} price={product.price} category={product.category} />
            }
        </div>
    )
}

export default ItemDetailContainer
