import React, { useState } from 'react'
import { useCartContext } from '../../context/CartContext'
import './cart.css'
import { NavLink } from 'react-router-dom';
import CartForm from './CartForm';
import ListCartItems from './ListCartItems';

const Cart = () => {
    
    const { cart } = useCartContext()

    const [formData, setFormData] = useState(initialState)
    const inputs = [
        {type:"text", name:"name", placeholder:"ingrese su nombre", value: formData.name},
        {type:"text", name:"lastname", placeholder:"ingrese su apellido", value: formData.lastname},
        {type:"text", name:"phone", placeholder:"ingrese su telefono", value: formData.phone},
        {type:"email", name:"email", placeholder:"ingrese su email", value: formData.email},
        {type:"email", name:"emailconfirmation", placeholder:"reingrese su email"}
    ]
    
    return (
        <>{cart.length === 0 &&
        <div className="row cart">
            <img src="https://i.gifer.com/XSNt.gif" alt="No hay nada en tu carrito"/>
            <h3>No hay nada en tu carrito</h3>
            <NavLink to="/"><button type="button" className="btn btn-productos btn-lg px-4 gap-3" data-bs-container="body" data-bs-toggle="popover" data-bs-placement="top" data-bs-content="Top popover">Ver productos</button></NavLink>
        </div>
        }
        {cart.length > 0 && 
        <div className="container">
            
                <ListCartItems/>
                <CartForm
                    formData={formData}
                    setFormData={setFormData}
                    initialState={initialState}
                    total={total}
                    inputs={inputs}
                />
        </div>
        }
        </>
    )
}

export default Cart

const initialState = {
    name: '',
    lastname: '',
    phone: '',
    emai:''
}
