import React from 'react'
import { useCartContext } from '../../context/CartContext'
import { getFirestore } from '../utils/getFirebase'
import firebase from 'firebase'

const CartForm = ({formData, setFormData, initialState, total}) => {
    
    const {clearCart, cart} = useCartContext()
    
    function handleChange(e) {
        setFormData(
            {
                ...formData,
                [e.target.name]: e.target.value,
            }
        )
    }
    function handleSubmit(e){
        e.preventDefault();
        const newOrder = {
            date: firebase.firestore.Timestamp.fromDate(new Date()),
            buyer: formData,
            item: cart,
            total: total,
        }
        const db = getFirestore();
        const orderCollection = db.collection('order');
        orderCollection
        .add(newOrder)
        .then(resp => alert (`Felicitaciones por tu compra. Tu número de orden es: ${resp.id}`))
        .catch(err => console.log(err))
        .finally(
            setFormData(initialState),
            clearCart()
        )
    }   

    return (
        <div>
            <form
            onSubmit={handleSubmit}
            onChange={handleChange}>
                {inputs.map(inp => <input type={inp.type} name={inp.name} placeholder={inp.placeholder} value={inp.value} />)}
                <button>confirmar compra</button> 
            </form>
        </div>
    )
}

export default CartForm
