import React, {useState} from 'react'

const CartForm = ({formData, setFormData, inputs, handleSubmit}) => {

    // function useTextInput(defaultValue) {
    //     const [val, setVal] = useState(defaultValue);
    
    //     return {
    //         onChange: evt => setVal(evt.target.value),
    //         value: val,
    //     };
    // }
    
    document.getElementById("purchase").disabled = true;
     
    function handleChange(e) {
        if (e.target.value !== "") {
            setFormData(
                {
                    ...formData,
                    [e.target.name]: e.target.value,
                }
            )
            document.getElementById("purchase").disabled = false;
        }
    }

    // const name = useTextInput("");
    // const lastName = useTextInput("");
    // const email = useTextInput("");
    // const emailRepeat = useTextInput("");
    // const phoneNumber = useTextInput("");

    return (
        <div className="form">
            <form
            onSubmit={handleSubmit}
            onChange={handleChange}>
                {inputs.map((inp) => {
                return(
                    <div className="mt-3">
                        <label>{inp.label}</label>
                        <input className="form-control" id={inp.id} type={inp.type} name={inp.name} placeholder={inp.placeholder} value={inp.value} />
                    </div>
                )})
                }
                <div className="mt-5">
                    <button id="purchase" className="btn btn-productos">
                        confirmar compra
                    </button>
                </div>
            </form>
        </div>
    )
}

export default CartForm
