import React, { useState } from 'react'
import { useCartContext } from '../../context/CartContext';
import CartForm from '../cart/CartForm'

const Checkout = () => {

    const { cart } = useCartContext()
    
    const total = 0;

    const [formData, setFormData] = useState(initialState)
    const inputs = [
        {type:"text", name:"name", placeholder:"ingrese su nombre", value: formData.name},
        {type:"text", name:"lastname", placeholder:"ingrese su apellido", value: formData.lastname},
        {type:"text", name:"phone", placeholder:"ingrese su telefono", value: formData.phone},
        {type:"email", name:"email", placeholder:"ingrese su email", value: formData.email},
        {type:"email", name:"emailconfirmation", placeholder:"reingrese su email"}
    ]

    return (
        <div>
            {cart.length > 0 &&
                <CartForm
                formData={formData}
                setFormData={setFormData}
                initialState={initialState}
                total={total}
                inputs={inputs}
            />
            }
            {cart.length === 0 &&
                <div>
                    <p>Gracias por tu compra. El id de tu compra es: </p>
                </div>
            }
        </div>
    )
}

export default Checkout

const initialState = {
    name: '',
    lastname: '',
    phone: '',
    emai:''
}
