import React, { useEffect, useState } from 'react'

const productsArray = [
    {id: 1, name: 'Producto 1', description: 'Descripción producto 1', price: 100},
    {id: 2, name: 'Producto 2', description: 'Descripción producto 1', price: 110},
    {id: 3, name: 'Producto 3', description: 'Descripción producto 1', price: 140},
    {id: 4, name: 'Producto 4', description: 'Descripción producto 1', price: 190},
];

function getProductos(id) {
    if (id===undefined) {
        return productsArray
    }else{
        return productsArray.find( productArray=> productArray.id === id)
    }
}

let tarea = new Promise((resolve) => {
    setTimeout(() => {
        resolve(getProductos(4));        
    }, 3000);
    //reject("error 404 ");
});

const ItemList = () => {

    useEffect(() => {
        tarea
        .then((resp)=> console.log(resp) )     //guardar en el estado
    }, [])
    
    return (
        <div>
            
        </div>
    )
}

export default ItemList
