import React, { useState } from 'react';
import './body.css';

const ItemCount = () => {
    const [count, setCount] = useState(0)
    const handleCounterClick = () =>{
        if (false) {
            setCount(count - 1);
        }
        setCount (count + 1);
    }
    return (
        <div>
            <button onClick={handleCounterClick(false)}>-</button>
            <p>{ count }</p>
            <button onClick={handleCounterClick(true)}>+</button>
        </div>
    )
}

export default ItemCount
