import React, { useEffect, useState } from 'react';
import ItemDetail from './ItemDetail';
import './body.css';
import { promiseDetail } from '../utils/promises';
import { useParams } from 'react-router-dom';

const ItemDetailContainer = () => {
    const [loading, setLoading] = useState(true)
    const [product, setProduct] = useState()
    const {id} = useParams()
    
    useEffect ( () => {
        const animals = [
            {id: 1, name: 'Medusa', category: 'Peces y animales marinos', slug: 'peces-y-animales-marinos', description: 'Nulla consequat massa quis enim. Donec pede justo.', price: 100, pictureUrl: 'https://images.pexels.com/photos/1683724/pexels-photo-1683724.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500'},
            {id: '2', name: 'Lechuza', category: 'Aves', slug: 'aves', description: 'Aliquam lorem ante, dapibus in, viverra quis, feugiat a, tellus.', price: 110, pictureUrl: 'https://images.pexels.com/photos/105804/pexels-photo-105804.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500'},
            {id: '3', name: 'Tigre de bengala', category: 'Mamíferos', slug: 'mamiferos', description: 'Aenean commodo ligula eget dolor. Aenean massa.', price: 140, pictureUrl: 'https://images.pexels.com/photos/145971/pexels-photo-145971.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500'},
            {id: '4', name: 'Camaleón', category: 'Reptiles', slug: 'reptiles', description: 'Donec quam felis, ultricies nec, pellentesque eu, pretium quis.', price: 190, pictureUrl: 'https://images.pexels.com/photos/3151186/pexels-photo-3151186.jpeg?auto=compress&cs=tinysrgb&h=750&w=1260'},
            // {id: '5', name: 'Chimpancé', category: 'Mamíferos', slug: 'mamiferos', description: 'Nulla consequat massa quis enim. Donec pede justo.', price: 150, pictureUrl: 'https://images.pexels.com/photos/33787/chimpanzee-sitting-sad-mammal.jpg?auto=compress&cs=tinysrgb&dpr=1&w=500'},
            // {id: '6', name: 'Rana marrón', category: 'Anfíbios', slug: 'anfibios', description: 'In enim justo, rhoncus ut, imperdiet a, venenatis vitae.', price: 180, pictureUrl: 'https://images.pexels.com/photos/129566/pexels-photo-129566.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500'},
            // {id: '7', name: 'Aguila', category: 'Aves', slug: 'aves', description: 'Vivamus elementum semper nisi. Aenean vulputate.', price: 120, pictureUrl: 'https://images.pexels.com/photos/158330/steppe-eagle-eagle-steppe-nature-158330.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500'},
            // {id: '8', name: 'Gecko', category: 'Anfíbios', slug: 'anfibios', description: 'Aenean leo ligula, porttitor eu, consequat vitae, eleifend ac.', price: 170, pictureUrl: 'https://images.pexels.com/photos/7329770/pexels-photo-7329770.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500'},
            // {id: '9', name: 'Pez payaso', category: 'Peces y animales marinos', slug: 'peces-y-animales-marinos', description: 'Donec quam felis, ultricies nec, pellentesque eu, pretium quis.', price: 190, pictureUrl: 'https://images.pexels.com/photos/4001917/pexels-photo-4001917.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500'},
            // {id: '10', name: 'Lagarto Marrón', category: 'Reptiles', slug: 'reptiles', description: 'Donec quam felis, ultricies nec, pellentesque eu, pretium quis.', price: 190, pictureUrl: 'https://images.pexels.com/photos/94271/gecko-rough-knob-lizard-australia-94271.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500'},
            // {id: '11', name: 'Koala', category: 'Mamíferos', slug: 'mamiferos', description: 'In enim justo, rhoncus ut, imperdiet a, venenatis vitae.', price: 180, pictureUrl: 'https://images.pexels.com/photos/7243633/pexels-photo-7243633.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500'},
            // {id: '12', name: 'Tortuga Marina', category: 'Reptiles', slug: 'reptiles', description: 'Aliquam lorem ante, dapibus in, viverra quis, feugiat a, tellus.', price: 110, pictureUrl: 'https://images.pexels.com/photos/4652070/pexels-photo-4652070.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500'},
            // {id: '13', name: 'Tiburón tigre', category: 'Peces y animales marinos', slug: 'peces-y-animales-marinos', description: 'Vivamus elementum semper nisi. Aenean vulputate.', price: 120, pictureUrl: 'https://images.pexels.com/photos/1700656/pexels-photo-1700656.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500'},
            // {id: '14', name: 'Salamandra', category: 'Anfíbios', description: 'Nulla consequat massa quis enim. Donec pede justo.', price: 150, pictureUrl: 'https://images.pexels.com/photos/5645950/pexels-photo-5645950.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500'},
            // {id: '15', name: 'Lemur', category: 'Mamíferos', slug: 'mamiferos', description: 'Aenean leo ligula, porttitor eu, consequat vitae, eleifend ac.', price: 170, pictureUrl: 'https://images.pexels.com/photos/7713938/pexels-photo-7713938.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500'},
            // {id: '16', name: 'Pájaro Carpintero', category: 'Aves', slug: 'aves', description: 'Vivamus elementum semper nisi. Aenean vulputate.', price: 120, pictureUrl: 'https://images.pexels.com/photos/5748862/pexels-photo-5748862.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500'},
        ];
        const productId = animals.filter(animal => animal.id === id)

            const getItem = (productos) => {
                return new Promise((resolve, reject) => {
                    setTimeout(() => {
                        resolve(...productos)
                    }, 2000)
                })
            } // Fin getItem

            getItem(productId).then(result => setProduct(result)).then(loading => setLoading(false)).catch(error => console.log(error.message))
    }, [id])


    // useEffect(()=>{
    //     promiseDetail.then((x)=>{
    //         setProduct(x)
    //         setLoading(false)
    //     }).catch((err)=>{console.log(err.message)})
    // },
    // [id])
    // console.log(typeof id);
    // console.log(product)
    // console.log(promiseDetail)
    // console.log(id)

    return (
        <div className="px-4 py-5 my-5 text-center">
            {
            loading
            ?
            <h2>Cargando detalle de producto...</h2>
            :
            <ItemDetail product={product} />
            }
        </div>
    )
}

export default ItemDetailContainer
