import React, { useEffect } from 'react'

const productObject = {id: 1, name: 'Producto 1', description: 'Nulla consequat massa quis enim. Donec pede justo.', price: 100, pictureUrl: 'https://images.pexels.com/photos/1683724/pexels-photo-1683724.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500'}

const promiseDetail = new Promise((resolve)=>{
    setTimeout(()=>{
        resolve(productObject)
    }, 3000)
})

const ItemDetailContainer = () => {
    const [product, setProduct] = useState({})
    useEffect(()=>{
        promiseDetail.then((x)=>{setProduct(x)}).catch((err)=>{console.log(err.message)})
    }, [])
    return (
        <div className="card product row">
            <img src={product.pictureUrl} className="card-img-top" alt={product.name} />
            <div className="card-body">
                <h4 className="card-title">{product.name}</h4>
                <p className="card-text">{product.description}</p>
                <h5>$ {product.price}</h5>
                <a href="/" className="btn btn-productos"><FontAwesomeIcon icon={faEye} /></a>
            </div>
        </div>
    )
}

export default ItemDetailContainer
