import React, { useState } from 'react'
import { useCartContext } from '../../context/CartContext';
import CartForm from '../cart/CartForm'
import { getFirestore } from '../utils/getFirebase'
import firebase from 'firebase'

const Checkout = () => {
    const [orderData, setOrderData] = useState({})
    const { clearCart, cart } = useCartContext()
    const [cartOrder, setCartOrder] = useState([])
    const [cartTotal, setCartTotal] = useState()
    
    const total = 0;

    const [formData, setFormData] = useState(initialState)
    const inputs = [
        {type:"text", name:"name", placeholder:"ingrese su nombre", value: formData.name},
        {type:"text", name:"lastname", placeholder:"ingrese su apellido", value: formData.lastname},
        {type:"text", name:"phone", placeholder:"ingrese su telefono", value: formData.phone},
        {type:"email", name:"email", placeholder:"ingrese su email", value: formData.email},
        {type:"email", name:"emailconfirmation", placeholder:"reingrese su email"}
    ]
    
    function handleSubmit(e){
        e.preventDefault();
        const newOrder = {
            date: firebase.firestore.Timestamp.fromDate(new Date()),
            buyer: formData,
            item: cart,
            total: total,
        }
        const db = getFirestore();
        const orderCollection = db.collection('order');
        orderCollection
        .add(newOrder)
        .then(resp => 
            setOrderData(resp),
            setCartTotal(total))
        .catch(err => console.log(err))
        .finally(
            setFormData(initialState),
            setCartOrder(cart),
            clearCart()
        )
    }
    console.log(cartTotal)

    return (
        <div>
            {cart.length > 0 &&
                <CartForm
                formData={formData}
                setFormData={setFormData}
                initialState={initialState}
                total={total}
                inputs={inputs}
                handleSubmit={handleSubmit}
            />
            }
            {cart.length === 0 &&
                <div>
                    <h1>Gracias por tu compra. Tu número de orden es: {orderData.id} </h1>
                    <h2>Resumen de compra</h2>
                    {cartOrder.map((element) =>{
                        return(
                            <div>
                                <p>{element.item.name}</p>
                                <p>{element.qty}</p>
                                <p>{element.item.price}</p>
                                <p>{orderData.total}</p>
                            </div>
                    )})}
                </div>
            }
        </div>
    )
}

export default Checkout

const initialState = {
    name: '',
    lastname: '',
    phone: '',
    emai:''
}
