import React from 'react'
import { useCartContext } from '../../context/CartContext'
import { getFirestore } from '../utils/getFirebase'

const CartForm = ({inputs, formData, setFormata, initialState, total}) => {

    const {clearCart, cart} = useCartContext()

    return (
        <div>
            <form>
                {inputs.map(inp => <input type={inp.type} placeholder={inp.placeholder} name={inp.name} value={inp.value} />)}
            </form>
        </div>
    )
}

export default CartForm
