import React from 'react'
import { useCartContext } from '../../context/CartContext'
import { getFirestore } from '../utils/getFirebase'
import firebase from 'firebase'

const CartForm = ({inputs, formData, setFormData, initialState, total}) => {

    const {clearCart, cart} = useCartContext()

    function handleChange(e) {
        setFormData(
            {
                ...formData,
                [e.target.name]: e.target.value
            }
        )
    }
    let newOrder = ''
    function handleSubmit(e){
        e.preventDefault()
        let newOrder = {
            buyer: formData,
            shop: cart,
            total: total,
            date: firebase.firestore.Timestamp.fromDate(new Date()),
        }
    }
    const db = getFirestore()
    const order = db.collection('orders')
    order
    .add(newOrder)
    .then(resp => alert(`la orden de compra es: ${resp.id}`))
    .catch(err => console.log(err))
    .finally(
        setFormata(initialState),
        clearCart
    )

    return (
        <div>
            <form
                onSubmit={handleSubmit}
                onChange={handleChange}
            >
                {inputs.map(inp => <input type={inp.type} placeholder={inp.placeholder} name={inp.name} value={inp.value} />)}
            </form>
            <button>Generar Orden de compra</button>
        </div>
    )
}

export default CartForm
