import React, { useEffect, useState } from 'react';
import ItemDetail from './ItemDetail';
import './body.css';
import { animals } from '../utils/promises';
import { useParams } from 'react-router-dom';

const ItemDetailContainer = () => {
    const [loading, setLoading] = useState(true)
    const [animalDetail, setAnimalDetail] = useState()
    const {id} = useParams()
    
    useEffect(()=>{
        const animalId = animals.find(animal => animal.id === id)

        const getAnimal = (animal) => {
            return new Promise((resolve, reject) => {
                setTimeout(() => {
                    resolve(animal)
                }, 2000)
            })
        } // Fin getItem

        getAnimal(animalId).then(result => setAnimalDetail(result)).then(loading => setLoading(false)).catch(error => console.log(error.message))

    }, [id])


    // useEffect(()=>{
    //     promiseDetail.then((x)=>{
    //         setProduct(x)
    //         setLoading(false)
    //     }).catch((err)=>{console.log(err.message)})
    // },
    // [id])
    // console.log(typeof id);
    // console.log(product)
    // console.log(promiseDetail)
    // console.log(id)

    return (
        <div className="px-4 py-5 my-5 text-center">
            {
            loading
            ?
            <h2>Cargando detalle de producto...</h2>
            :
            <ItemDetail animalDetail={animalDetail} />
            }
        </div>
    )
}

export default ItemDetailContainer
