import React, { useEffect, useState } from 'react';
import ItemDetail from './ItemDetail';
import './body.css';
import { promiseList } from '../utils/promises'
import { useParams } from 'react-router-dom';

const ItemDetailContainer = () => {
    const [loading, setLoading] = useState(true)
    const [product, setProduct] = useState()
    const {id} = useParams()

    const animal = promiseList.filter(a => a.id===id)
    const getAnimal = (productos) => {
        return new Promise((resolve) => {
            setTimeout(() => {
                resolve(...productos)
            }, 2000)
        })
    }

    useEffect(()=>{
        getAnimal(animal).then(x => setProduct(x)).then(setLoading(false)).catch(err => console.log(err.message))        
    },[id])


    
    // useEffect(()=>{
    //     promiseDetail.then((x)=>{
    //         setProduct(x)
    //         setLoading(false)
    //     }).catch((err)=>{console.log(err.message)})
    // },
    // [id])
    
    console.log(product)

    return (
        <div className="px-4 py-5 my-5 text-center">
            {
            loading
            ?
            <h2>Cargando detalle de producto...</h2>
            :
            <ItemDetail product={product} />
            }
        </div>
    )
}

export default ItemDetailContainer
